"""print(type(9))print(type(9.2))print(type("asdadasdas"))print(type('c'))   #type fonksiyonu girilen değişkenin tipini verir.print("hello world")"""#-------------------------------------------------------------------a = "*selamlar efendim*      "len(a)    #uzunluğu verir.a.upper()   #tüm harfleri büyük harf yapar.a.lower()   #tüm harfleri küçük harf yapar.a.islower()   #tüm harfler küçük mü diye kontrol eder.a.isupper()   #tüm harfler büyük mü diye kontrol eder.a.strip()     #baştaki ve sondaki spaceleri kaldırıyor.a.strip("* ") #baştaki ve sondaki yıldız işaretini kaldırır.a.replace("e","i")   #e harfleri yerine i harflerini getirir.dir(a)